function y=steadystate_nonlinear_VSM_VC_noPLL(x,conv)
% Steady-state nonlinear equations for a grid-forming VSM VC converter without PLL

y= [conv.parameters.kdrpq.*conv.ss.qref0+conv.ss.vref0+x(1).*conv.parameters.lg.*conv.ss.Omegag0-x(1).^2.*conv.parameters.kdrpq.*conv.parameters.lg.*conv.ss.Omegag0+x(1).*conv.parameters.ls.*conv.ss.Omegag0+1./(conv.parameters.lg+conv.parameters.ls).*conv.parameters.rg./conv.ss.Omegag0.*(x(1).*(conv.parameters.rg+conv.parameters.rs)+conv.ss.vnq0.*cos(x(2))-conv.ss.vnd0.*sin(x(2)))+ ...
  1./(conv.parameters.lg+conv.parameters.ls).*conv.parameters.rs./conv.ss.Omegag0.*(x(1).*(conv.parameters.rg+conv.parameters.rs)+conv.ss.vnq0.*cos(x(2))-conv.ss.vnd0.*sin(x(2)))-conv.parameters.kdrpq.*conv.parameters.lg.*(conv.parameters.lg+conv.parameters.ls).^(-2)./conv.ss.Omegag0.*(x(1).*(conv.parameters.rg+conv.parameters.rs)+conv.ss.vnq0.*cos(x(2))-conv.ss.vnd0.*sin( ...
  x(2))).^2+cos(x(2)).*((-1+x(1).*conv.parameters.kdrpq).*conv.ss.vnd0+conv.parameters.kdrpq./(conv.parameters.lg+conv.parameters.ls).*conv.ss.vnq0./conv.ss.Omegag0.*(x(1).*(conv.parameters.rg+conv.parameters.rs)+conv.ss.vnq0.*cos(x(2))-conv.ss.vnd0.*sin(x(2))))+sin( ...
  x(2)).*(-conv.ss.vnq0+x(1).*conv.parameters.kdrpq.*conv.ss.vnq0+conv.parameters.kdrpq./(conv.parameters.lg+conv.parameters.ls).*conv.ss.vnd0./conv.ss.Omegag0.*(-x(1).*(conv.parameters.rg+conv.parameters.rs)-conv.ss.vnq0.*cos(x(2))+conv.ss.vnd0.*sin(x(2)))),1./conv.parameters.ta.*(conv.ss.pref0-x(1).^2.*conv.parameters.rg-conv.parameters.kdrpOmega.* ...
  conv.ss.Omegag0+conv.parameters.kdrpOmega.*conv.ss.Omegaref0-(conv.parameters.lg+conv.parameters.ls).^(-2).*conv.parameters.rg.*conv.ss.Omegag0.^(-2).*(x(1).*(conv.parameters.rg+conv.parameters.rs)+conv.ss.vnq0.*cos(x(2))-conv.ss.vnd0.*sin(x(2))).^2+sin(x(2)).*(x(1).*conv.ss.vnd0+1./(conv.parameters.lg+conv.parameters.ls).* ...
  conv.ss.vnq0./conv.ss.Omegag0.*(x(1).*(conv.parameters.rg+conv.parameters.rs)+conv.ss.vnq0.*cos(x(2))-conv.ss.vnd0.*sin(x(2))))-cos(x(2)).*(x(1).*conv.ss.vnq0+1./(conv.parameters.lg+conv.parameters.ls).*conv.ss.vnd0./conv.ss.Omegag0.*(-x(1).*(conv.parameters.rg+conv.parameters.rs)-conv.ss.vnq0.*cos( ...
  x(2))+conv.ss.vnd0.*sin(x(2)))))];
end